mapper.select:
select
, u.uname as up_uname, u.unick as up_unick , s.uname as su_uname, s.unick as su_unick , ou.uname as comp_uname, ou.unick as comp_unick , o.order_no as order_num from td_complaint c left join td_user u on u.uid= c.update_by left join td_user s on s.uid= c.cuid left join td_user ou on ou.uid= c.uid left join td_order o on o.order_id= c.order_id where 1=1
and c.status = #{status,jdbcType=TINYINT}
