mapper.select:
    select 
    
    from td_user_account_log
    where 1=1 
    
        and uid=#{uid,jdbcType=INTEGER}
    
    
		and create_time  >= 
		#{beginDate,jdbcType=TIMESTAMP}
	
	
		and create_time  < 
		#{endDate,jdbcType=TIMESTAMP}
	
	
		and type = #{type,jdbcType=TINYINT}
	
	
		and upamount  >=  0 
		and upamount  <  0 
	
    order by create_time desc

    select count(id)
    from td_user_account_log
    where 1=1 
    
        and uid=#{uid,jdbcType=INTEGER}
    
    
		and create_time  >= 
		#{beginDate,jdbcType=TIMESTAMP}
	
	
		and create_time  < 
		#{endDate,jdbcType=TIMESTAMP}
	
	
		and type = #{type,jdbcType=TINYINT}
	
	
		and upamount  >=  0 
		and upamount  <  0

select count(*) from ( select count(*) from td_user_account_log as g left join td_user as u on g.uid = u.uid where 1=1 and type = 2
and u.uname like CONCAT('%',#{username,jdbcType=VARCHAR},'%')
and g.create_time >= #{beginDate,jdbcType=TIMESTAMP}
and g.create_time < #{endDate,jdbcType=TIMESTAMP}
group by g.uid) as t
