mapper.select:
select
,
, u.uname as username from td_user_message as m left join td_user u on u.uid=m.uid where m.id = #{id,jdbcType=INTEGER}

select count(m.id)
from td_user_message as m

    left join td_user u on u.uid= m.uid

where 1=1 

    and m.id=#{id,jdbcType=INTEGER}


    and u.uname like CONCAT('%',#{username,jdbcType=VARCHAR},'%' )


    and m.msg_type=#{msgType,jdbcType=TINYINT}


    and m.status=#{status,jdbcType=TINYINT}


    and m.title like CONCAT('%',#{title,jdbcType=VARCHAR},'%' )


    and m.create_time >= #{beginDate,jdbcType=DATE}


    and m.create_time <= #{endDate,jdbcType=DATE}

select 


    , u.uname as username

from td_user_message as m

    left join td_user u on u.uid= m.uid

where 1=1 

    and m.id=#{id,jdbcType=INTEGER}


    and u.uname like CONCAT('%',#{username,jdbcType=VARCHAR},'%' )


    and m.msg_type=#{msgType,jdbcType=TINYINT}


    and m.status=#{status,jdbcType=TINYINT}


    and m.title like CONCAT('%',#{title,jdbcType=VARCHAR},'%' )



    and m.create_time >= #{beginDate,jdbcType=DATE}


    and m.create_time <= #{endDate,jdbcType=DATE}
