mapper.select:
select 

from td_order o
where o.order_id = #{orderId,jdbcType=INTEGER}

    select 
    
    
        , u.uname as up_uname, u.unick as up_unick
    
    
        , s.order_sku_id, s.product_id, s.product_name, s.display_specifications, s.price, s.quantity,s.product_image
        
    
    from td_order o
    
        left join td_user u on u.uid= o.update_by
    
    
        left join td_order_sku s on s.order_id= o.order_id
    
    where 1=1 
    
        and o.order_no=#{orderNo,jdbcType=VARCHAR}
    
    
        and o.order_type=#{orderType,jdbcType=TINYINT}
    
    
        and o.order_status=#{orderStatus,jdbcType=TINYINT}
    
    
        and o.pay_status=#{payStatus,jdbcType=TINYINT}
    
    
        and o.shipment_status=#{shipmentStatus,jdbcType=TINYINT}
    
    
        and o.user_id=#{uid,jdbcType=INTEGER}
    
    
    	
	    	
	    		and o.pay_status=2
	    	
	    	
	    		and o.pay_status=1 and o.shipment_status=2
	    	
	    	
	    		and o.order_status=3
	    	
	    	
	    		and o.order_status=4 and o.commented=0
	    	
	    	
    	
    
    
        and o.supplier_id=#{supplierId,jdbcType=INTEGER}
    
    
        and o.joint_id=#{jointId,jdbcType=INTEGER}
    
    
        and o.update_time  <  #{shippmentTime,jdbcType=TIMESTAMP}
    
    
        and o.update_time  <  #{receiptTime,jdbcType=TIMESTAMP}
    
    
        and (o.order_no  like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%') or s.product_name like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%'))
    
    order by o.create_time desc

    select count(o.order_id)
    from td_order o
    
    	left join td_order_sku s on s.order_id= o.order_id
    
    where 1=1 
    
        and o.order_no=#{orderNo,jdbcType=VARCHAR}
    
    
        and o.order_type=#{orderType,jdbcType=TINYINT}
    
    
        and o.order_status=#{orderStatus,jdbcType=TINYINT}
    
    
        and o.pay_status=#{payStatus,jdbcType=TINYINT}
    
    
        and o.shipment_status=#{shipmentStatus,jdbcType=TINYINT}
    
    
        and o.user_id=#{uid,jdbcType=INTEGER}
    
    
    	
	    	
	    		and o.pay_status=2
	    	
	    	
	    		and o.pay_status=1 and o.shipment_status=2
	    	
	    	
	    		and o.order_status=3
	    	
	    	
	    		and o.order_status=4 and o.commented=0
	    	
	    	
    	
    
    
        and o.supplier_id=#{supplierId,jdbcType=INTEGER}
    
    
        and o.joint_id=#{jointId,jdbcType=INTEGER}
    
    
        and o.update_time  <  #{shippmentTime,jdbcType=TIMESTAMP}
    
    
        and o.update_time  <  #{receiptTime,jdbcType=TIMESTAMP}
    
    
        and (o.order_no  like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%') or s.product_name like CONCAT('%',#{keyword,jdbcType=VARCHAR},'%'))
