mapper.insert:
insert into td_product_type (id, name, parent_id, 
  create_time, update_time, update_by, 
  status, image_url, sort
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=INTEGER}, 
  #{status,jdbcType=TINYINT}, #{imageUrl,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}
  )

mapper.select:
select count(t.id) from td_product_type as t LEFT OUTER JOIN td_product_type_attribute ta ON t.id=ta.type_id where 1=1
and t.parent_id=#{parentId,jdbcType=INTEGER}
and status=#{status,jdbcType=INTEGER}
order by a.sort desc
order by a.sort asc

select
,ta.id as ta_id ,ta.type_id as ta_typeid ,ta.attri_id as ta_attriid from td_product_type as t LEFT OUTER JOIN td_product_type_attribute ta ON t.id=ta.type_id where 1=1
and t.parent_id=#{parentId,jdbcType=INTEGER}
and status=#{status,jdbcType=INTEGER}
order by sort desc
order by sort asc
